.filters-wrapper{
    display: flex;
    justify-content: space-between;
    padding: 1rem 2rem;
    border: 1px solid rgba(133, 133, 133, 0.2);
    width: 100vw;
    margin: 0 auto;
}

.showfilter-wrapper{
    /* display: none; */
    position: absolute;
    background-color: rgba(255, 255, 255, 0.95);
    visibility: hidden;
    right: 0;
    width: 30vw;
    height: 90vh;
    z-index: 1000;
    border: 1px solid rgba(133, 133, 133, 0.2);
    border-top: none;
    padding: 2rem;
    margin-bottom: 2rem;
}

.sideBarFilter ul {
    list-style: none;
}
.sideBarFilter ul li {
    padding: 1rem 2rem;
    border-top: 1px solid rgba(0, 0, 0, .2);
    font-size: 1;
    font-family: "Roboto";
    color: black;
    align-self: center;
    cursor: pointer;
    transition: 0.3s ease-in-out;
    display: flex;
    justify-content: space-between;
}
.sideBarFilter ul li:hover {
    background-color: rgb(17, 17, 17);
    color: white;
    transition: 0.3s ease-in-out;
}


.fadeFilters{
    animation: fadeFilters 0.5s ease-in-out forwards;
}

.fadeOutFilter{
    animation: fadeOutFilter 0.5s ease-in-out forwards;
}

.ais-Stats-text{
    font-family: 'Roboto';
    font-weight: bold;
    font-size: 1.2rem;
    color: black;
}

.ais-ClearRefinements-button{
    padding: 0.6rem 2rem;
    border-radius: 0px !important;
    background-color: white;
    font-size: 1;
    font-family: "Roboto";
    font-style: italic;
    font-weight: 300;
    color: black !important;
    opacity: 1;
    align-self: center;
    cursor: pointer;
    transition: 0.3s ease-in-out;
    cursor: pointer;

}

.ais-ClearRefinements-button:after {
    margin-top: 0.2rem;
    display:block;
    content: '';
    border-bottom: 1px solid rgba(0, 0, 0); 
    transform: scaleX(1.1);  
    transition: transform 0.3s ease-in-out;
}

.ais-ClearRefinements-button:hover:after { 
    transform: scaleX(0); 
}

.ais-ClearRefinements-button:hover {
    cursor: pointer;
    background-color: white;;
    color: black !important;
}

.btn-filter{
    padding: 0.6rem 2rem;
    border: 1px solid rgba(0, 0, 0, .5);
    font-size: 1;
    font-family: "Roboto";
    color: black;
    align-self: center;
    cursor: pointer;
    transition: 0.3s ease-in-out;
}

.btn-filter:hover {
    background-color: rgb(17, 17, 17);
    color: white;
    transition: 0.3s ease-in-out;
}

.btn-filter i{
  margin-left: 0.5rem;
}

.left-panel{
    padding: 2rem;
}

 .category-wrapper, .gender-wrapper, .color-wrapper, .size-wrapper{
    /* border-right: 1px solid rgba(0, 0, 0, .5); */
    visibility: hidden;
    transform: translateX(100%);
    position: absolute;
    top: 10rem;
}

.btn-backFilter{
    /* padding: 0.6rem 2rem; */
    width: 120px;
    /* display: flex;
    justify-content: space-between; */
    border-radius: 0px !important;
    background-color: transparent;
    font-family: "Roboto";
    font-style: italic;
    font-weight: 300;
    color: black !important;
    opacity: 1;
    align-self: center;
    cursor: pointer;
    transition: 0.3s ease-in-out;
    cursor: pointer;
    margin-bottom: 2rem;
}

.btn-backFilter:after {
    margin-top: 0.3rem;
    display:block;
    content: '';
    border-bottom: 1px solid rgba(0, 0, 0); 
    transform: scaleX(1.1);  
    transition: transform 0.3s ease-in-out;
}

.btn-backFilter:hover:after { 
    transform: scaleX(0); 
}

.btn-backFilter i{
    margin-right: 2rem;
}


.left-panel h2 {
    font-family: 'Castoro', serif;
    margin-bottom: 1rem;
    margin: 1rem 0;

}

.ais-RefinementList-list{
    margin: 1rem 0rem;
}

.ais-RefinementList-labelText{
    color: black;
    font-family: "Roboto";
    font-size: 1rem;
    font-weight: 300;
}

.ais-RefinementList-count{
    width: 30px;
    height: 20px;
    font-size: .6rem;
    font-style: italic;
    color: white;
    background-color: rgba(78, 78, 78, 0.295);
    border-radius: 5px;
    align-self: center;
}


.currentRefinment-filters{
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}


.hexColor-list .ais-RefinementList-list {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 1rem;
}
.hexColor-list .ais-RefinementList-list .ais-RefinementList-item div {
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.hexColor-list .ais-RefinementList-list label{
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    font-size: 0.8rem;
    margin: 0.2rem auto 0.1rem auto; 
    color: black;
    font-family: "Roboto";

}


.colorInput[type="color"] {
	-webkit-appearance: none;
    border: none;
    border-radius: 50%;
	width: 32px;
    height: 32px;
    align-self: center;
}
.colorInput[type="color"]::-webkit-color-swatch-wrapper {
    padding: 0;
}
.colorInput[type="color"]::-webkit-color-swatch {
    border: 1px solid rgba(0, 0, 0, .5) ;
    border-radius: 50%;
}


/* CURRENT REFINEMENT FILTERS  */

.current-refinements li{
    cursor: pointer;
    padding: 0.5rem;
    border: 1px solid rgba(8, 8, 8, 0.9);
   list-style: none;
border-radius: 0;
   margin: 0.3rem;
   font-family: 'Roboto';
   font-size: 0.8rem;
   background-color: rgb(219, 219, 219);
   font-style: italic;
   transition: all 0.3s ease-in-out;
}
.current-refinements li:hover {
   background-color: rgb(255, 255, 255);
   transform: scale(1.02);
   transition: all 0.3s ease-in-out;  
}

.current-refinements  li  span {
    color: black;
    font-size: 0.8rem;
}

.btnCloseRefinements{
    color: black;
    border: none;
    background: transparent;
    margin-left: 0.4rem;
}

@keyframes fadeFilters {
    0%{
        transform: translateX(100%);
        visibility: visible;
        opacity: 0.2;
    }
    100%{
        transform: translateX(0%);
        visibility: visible;
        opacity: 1;
    }
}
@keyframes fadeOutFilter {
    0%{
        transform: translateX(0%);
        visibility: visible;
        opacity: 1;
    }
    100%{
        transform: translateX(100%);
        visibility: hidden;
        opacity: 0;
    }
}


@media screen and (max-width: 768px){
    .left-panel {
        grid-template-columns: repeat(1, 1fr);  
        padding: 0rem;   
    }
    .left-panel h2{
        margin: 1rem 0rem;
    }

    .ais-RefinementList-list {
        margin: 1rem 0rem;
        font-size: 0.6rem;
    }
   }
